import Cors from 'micro-cors'

const cors = Cors()

const superagent = require('superagent');
const handler = (req, res) => {
  let events
  superagent
    .get('https://insights-api.newrelic.com/v1/accounts/2482859/query?nrql=SELECT+%60_raw%60%2C%60_sourcetype%60%2C%60source%60+FROM+splunkUpdateLogs%2C+splunkApacheAccess%2C+splunkApacheErrors%2C+splunkAuthLogs%2C+splunkHttp%2C+splunkKernel%2C+splunkSystemctl+WHERE+%60_raw%60+NOT+LIKE+%27%25CRON%25%27+LIMIT+3+')
    .set('X-Query-Key', 'NRIQ-j7zJFckfarn9jsOB-nPscK5H6z0QCgHd')
    .set('Accept', 'application/json')
    .end((err, response) => {
      events = JSON.parse(response.text).results[0].events
      console.log(" => Events 1 : ",events[0]);
      console.log(" => Events 2 : ",events[1]);
      console.log(" => Events 3 : ",events[2]);
      let eval1 = events[0]._raw;
      let eval2 = events[1]._raw;
      let eval3 = events[2]._raw;
      return res.json(events);
    });
};

export default cors(handler);
